mapper.select:
SELECT TITLE
FROM CONTEST
WHERE ID = #{value}

SELECT ID
FROM CONTEST
WHERE MEMBEREMAIL = #{value}
ORDER BY ID DESC
LIMIT 1;

SELECT FILEORINAME, FILEURL
FROM CONTESTFILE
WHERE BNO = #{value}

mapper.update:
UPDATE CONTEST
SET TITLE = #{title}, CONTESTTYPE = #{contesttype}, COMPANY = #{company}, SERVICEINFO = #{serviceinfo}, SECTOR = #{sector}, IDEA = #{idea}, BRIEFING = #{briefing}
WHERE ID = #{id}
